    @Override
    public void initialize(URL location, ResourceBundle resources) {
        try {
            allWeeklyApptmts = SelectDB.selectAllCompleteAppointments();
        } catch (SQLException e) {
            e.printStackTrace();
        }

        LocalDate dateX = LocalDate.now();
        year = dateX.getYear();

        setMonthLabel(dateX.getMonthValue());
        yearLbl1.setText(String.valueOf(year));
        yearLbl2.setText(String.valueOf(year));

        try {
            if (Calendar.dayOfWeekNum(1, 1, year) == 0) {
                sundayStartMonth(dateX);
            }
            else if (Calendar.dayOfWeekNum(1, 1, year) == 1) {
                mondayStartMonth(dateX);
            }
            else if (Calendar.dayOfWeekNum(1, 1, year) == 2) {
                tuesdayStartMonth(dateX);
            }
            else if(Calendar.dayOfWeekNum(1,1, year) == 3) {
                wednesdayStartMonth(dateX);
            }
            else if(Calendar.dayOfWeekNum(1,1, year) == 4) {
                thursdayStartMonth(dateX);
            }
            else if(Calendar.dayOfWeekNum(1,1, year) == 5) {
                fridayStartMonth(dateX);
            }
            else if(Calendar.dayOfWeekNum(1,1, year) == 6) {
                saturdayStartMonth(dateX);
            }
            else {
                Alert alert = new Alert(Alert.AlertType.ERROR);
                alert.setTitle("Error");
                alert.setHeaderText("Date Error");
                alert.setContentText("Please check the current date settings!");
                alert.showAndWait();
            }

        } catch (Exception e) {
            e.printStackTrace();
        }
    }
